package variable;

public class Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int num1 = 2;
			int num2 = 3;
			//3 + 5 = 8
			System.out.println( num1 + " + " +num2 + " = " + (num1+num2) );
			System.out.println( num1 + " - " +num2 + " = " + (num1-num2) );
			System.out.println( num1 + " * " +num2 + " = " + (num1*num2) );
			System.out.println( num1 + " / " +num2 + " = " + (double)num1/num2 );
			
	}

}
